import { useEffect , useState} from 'react';

function App() {
  const [convert,setConvert] = useState(false);
  const [currencies,setcurrencies] = useState([]);
  const [query,setQuery] = useState({from:'huf'});
  const [query2,setQuery2] = useState({from:'eur'});
  console.log(currencies);

  const urlParams = new URLSearchParams(Object.entries(query));
  const urlParams2 = new URLSearchParams(Object.entries(query2));

  useEffect(
    ()=>{
      fetch('/convert?' + urlParams + "?" + urlParams2)
    },[]
  );



  useEffect(
    ()=>{
      fetch('/symbols')
      .then(res=>res.json())
      .then(data=>setcurrencies(data))
    },[]
  );


  const currenciesToSelect = currencies.map(item=>{
    return <option>{item}</option>
  })

  return (
    <div>
      <h1>Online valutaváltó</h1>
      <form>
        <label>Amount</label>
        <input type='number'/>

        <label>from this currency</label>
        <select>
          {currenciesToSelect}
        </select>

        <label>to this currency</label>
        <select>
          {currenciesToSelect}
        </select>
        <button>GO</button>
      </form>
      <p>x money = y another money</p>
    </div>
  );
}

export default App;
